mapper.select:
select a.* from m_permission a where a.enabled = 1 and a.id in (
  select b.permission_id from m_permission_group_rel b where b.group_id = #{group_id}
)

select * from m_permission where id in (
  select permission_id from m_permission_group_rel where group_id in (
    select id from m_permission_group where id in (
      select group_id from m_user_group_rel where user_id = #{user_id}
    )
  )
)
union all
select * from m_permission where id in(
  select permission_id from m_user_permission_rel where user_id = #{user_id}
)
